import React, { useState, useEffect } from "react";
import "./styles.css";
import initialTransactions from "./InitialTransaction";

function getItemsFromLocalStorage() {
  const saved = localStorage.getItem("transactions");
  const initialValue = JSON.parse(saved) || initialTransactions;
  return initialValue;
}

export default function Transaction() {
  const [transactions, setTransactions] = useState(
    getItemsFromLocalStorage
  );
  const [itemId, setItemId] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [msg, setMsg] = useState("none");
  const [summarizedData, setSummarizedData] = useState(null); // add this line


  function addTransaction() {
    const newTransaction = {
      itemId: itemId,
      amount: amount,
      category: category,
      date: date,
      description: description
    };
    setTransactions([...transactions, newTransaction]);
    setItemId("");
    setAmount("");
    setCategory("");
    setDate("");
    setDescription("");
  }

  function deleteTransaction(index) {
    const newTransactions = [...transactions];
    newTransactions.splice(index, 1);
    setTransactions(newTransactions);
  }

  const summarizeByDate = (transactions) => {
    const summarizedData = {};
    for (const transaction of transactions) {
      const date = transaction.date;
      const amount = Number(transaction.amount);
      if (summarizedData[date]) {
        summarizedData[date] += amount;
      } else {
        summarizedData[date] = amount;
      }
    }
    return summarizedData;
  };

  function summarizeByMonth() {
    const summary = {};
    transactions.forEach((transaction) => {
      const [year, month] = transaction.date.split("-");
      const date = `${year}-${month}`;
      const amount = parseFloat(transaction.amount);
      if (summary[date]) {
        summary[date] += amount;
      } else {
        summary[date] = amount;
      }
    });
    return summary;
  }

  function summarizeByYear() {
    const summary = {};
    transactions.forEach((transaction) => {
      const year = transaction.date.split("-")[0];
      const amount = parseFloat(transaction.amount);
      if (summary[year]) {
        summary[year] += amount;
      } else {
        summary[year] = amount;
      }
    });
    return summary;
  }

  function summarizeByCategory() {
    const summary = {};
    transactions.forEach((transaction) => {
      const category = transaction.category;
      const amount = parseFloat(transaction.amount);
      if (summary[category]) {
        summary[category] += amount;
      } else {
        summary[category] = amount;
      }
    });
    return summary;
  }

  function handleChange(event) {
    const { name, value } = event.target;
    if (name === "itemId") {
      setItemId(value);
    } else if (name === "amount") {
      setAmount(value);
    } else if (name === "category") {
      setCategory(value);
    } else if (name === "date") {
      setDate(value);
    } else if (name === "description") {
      setDescription(value);
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    addTransaction();
  }

  function handleDelete(index) {
    deleteTransaction(index);
  }

  function quit() {
    const confirmed = window.confirm('Are you sure you want to quit?');
    if (confirmed) {
      window.close();
    }
  }

  const onSummarizeByDateClick = () => {
    setSummarizedData(summarizeByDate(transactions));
  };
  
  return (
    <div className="App">
      <h1>Finance Tracker</h1>
      <div className="form">
        <label>
          Item ID:
          <input type="text" name="itemId" value={itemId} onChange={handleChange} />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={amount} onChange={handleChange} />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={category} onChange={handleChange} />
        </label>
        <label>
          Date:
          <input type="date" name="date" value={date} onChange={handleChange} />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={description} onChange={handleChange} />
        </label>
        <button onClick={handleSubmit}>Add Transaction</button>
      </div>
      <div>
        <h2>Transactions</h2>
        {transactions.map((transaction, index) => (
          <div key={index}>
            <p>{`Item ID: ${transaction.itemId}`}</p>
            <p>{`Amount: ${transaction.amount}`}</p>
            <p>{`Category: ${transaction.category}`}</p>
            <p>{`Date: ${transaction.date}`}</p>
            <p>{`Description: ${transaction.description}`}</p>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
      <button onClick={onSummarizeByDateClick}>Summarize by Date</button>
      <button onClick={summarizeByMonth}>Summarize by Month</button>
      <button onClick={summarizeByYear}>Summarize by Year</button>
      <button onClick={summarizeByCategory}>Summarize by Category</button>
      <button onClick={quit}>Quit</button>
    </div>
  );
        }
