import initialTransactions from "./InitialTransaction"
import React,{useState,useEffect} from "react";
import "./styles.css";
  
function getItemsFromLocalStorage() {
    // getting stored value
    const saved = localStorage.getItem("items");
    const initialValue = JSON.parse(saved)||[];
    // relabel the keys from 0 to length-1
    for(let i=0;i<initialValue.length;i++){
      initialValue[i]['key']=i
    }
    return initialValue || [];
  }
  
  export default function transaction() {
  
    let [items, setItems] = useState(getItemsFromLocalStorage);
    let [numKeys,setNumKeys] = useState(() => items.length)
    let [msg,setMsg] = useState('none');
  
    function add_item() {
      // add an item to the todolist
      const item = document.getElementById("item").value;
      let newItem = {
        key: numKeys,
        desc: item,
        amount: document.getElementById("amount").value,
        completed: false
      };
      document.getElementById("item").value = ""
      document.getElementById("amount").value = ""
      setNumKeys(numKeys+1)
      setItems([newItem,...items]); // using the spread operator ...
    }
  
    function deleteItem(key){
      console.log(key)
      const newItems = items.filter((x)=> x['key']!== key)
      setItems(newItems)
      setNumKeys(numKeys-1)
    }
  
    useEffect(() => {
      // storing items if items changes value
      localStorage.setItem("items", JSON.stringify(items));
    }, [items]);
    const handleKeyDown = (event) => {
      if (event.key === 'Enter') {
        add_item();
      }
    };
  
    function toggleCompleted(item){
      item.completed = !item.completed;
      setItems([...items]);  // force redraw of screen
    }

  return (
    <div className="App">
      <h1>Finance Tracker</h1>
      <div className="form">
        <label>
          Item ID:
          <input type="text" name="itemId" value={itemId} onChange={handleChange} />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={amount} onChange={handleChange} />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={category} onChange={handleChange} />
        </label>
        <label>
          Date:
          <input type="date" name="date" value={date} onChange={handleChange} />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={description} onChange={handleChange} />
        </label>
        <button onClick={handleSubmit}>Add Transaction</button>
      </div>
      <div>
        <h2>Transactions</h2>
        {transactions.map((transaction, index) => (
          <div key={index}>
            <p>{`Item ID: ${transaction.itemId}`}</p>
            <p>{`Amount: ${transaction.amount}`}</p>
            <p>{`Category: ${transaction.category}`}</p>
            <p>{`Date: ${transaction.date}`}</p>
            <p>{`Description: ${transaction.description}`}</p>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Category</h2>
        {Object.entries(summarizeByCategory()).map(([category, totalAmount]) => (
          <div key={category}>
            <p>{`${category}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
    </div>
  );
        }
