import React, { useState, useEffect } from "react";
import handleShowTransactions from './handleShowTransactions';
import "./styles.css";

function getItemsFromLocalStorage() {
  const saved = localStorage.getItem("transactions");
  const initialValue = JSON.parse(saved) || [];
  return initialValue;
}

export default function Transaction() {
  const [transactions, setTransactions] = useState(
    getItemsFromLocalStorage
  );
  const [itemId, setItemId] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [msg, setMsg] = useState("none");

  function addTransaction() {
    const newTransaction = {
      itemId: itemId,
      amount: amount,
      category: category,
      date: date,
      description: description
    };
    setTransactions([...transactions, newTransaction]);
    setItemId("");
    setAmount("");
    setCategory("");
    setDate("");
    setDescription("");
  }

  function deleteTransaction(index) {
    const newTransactions = [...transactions];
    newTransactions.splice(index, 1);
    setTransactions(newTransactions);
  }

  function summarizeByDate() {
    const summary = {};
    transactions.forEach((transaction) => {
      const date = transaction.date;
      const amount = parseFloat(transaction.amount);
      if (summary[date]) {
        summary[date] += amount;
      } else {
        summary[date] = amount;
      }
    });
    return summary;
  }

  function summarizeByMonth() {
    const summary = {};
    transactions.forEach((transaction) => {
      const [year, month] = transaction.date.split("-");
      const date = `${year}-${month}`;
      const amount = parseFloat(transaction.amount);
      if (summary[date]) {
        summary[date] += amount;
      } else {
        summary[date] = amount;
      }
    });
    return summary;
  }

  function summarizeByYear() {
    const summary = {};
    transactions.forEach((transaction) => {
      const year = transaction.date.split("-")[0];
      const amount = parseFloat(transaction.amount);
      if (summary[year]) {
        summary[year] += amount;
      } else {
        summary[year] = amount;
      }
    });
    return summary;
  }

  function summarizeByCategory() {
    const summary = {};
    transactions.forEach((transaction) => {
      const category = transaction.category;
      const amount = parseFloat(transaction.amount);
      if (summary[category]) {
        summary[category] += amount;
      } else {
        summary[category] = amount;
      }
    });
    return summary;
  }

  function handleChange(event) {
    const { name, value } = event.target;
    if (name === "itemId") {
      setItemId(value);
    } else if (name === "amount") {
      setAmount(value);
    } else if (name === "category") {
      setCategory(value);
    } else if (name === "date") {
      setDate(value);
    } else if (name === "description") {
      setDescription(value);
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    addTransaction();
  }

  function handleDelete(index) {
    deleteTransaction(index);
  }

  return (
    <div className="App">
      <h1>Finance Tracker</h1>
      <div className="form">
        <label>
          Item ID:
          <input type="text" name="itemId" value={itemId} onChange={handleChange} />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={amount} onChange={handleChange} />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={category} onChange={handleChange} />
        </label>
        <label>
          Date:
          <input type="date" name="date" value={date} onChange={handleChange} />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={description} onChange={handleChange} />
        </label>
        <button onClick={handleSubmit}>Add Transaction</button>
      </div>
      <div>
        <h2>Transactions</h2>
        {transactions.map((transaction, index) => (
          <div key={index}>
            <p>{`Item ID: ${transaction.itemId}`}</p>
            <p>{`Amount: ${transaction.amount}`}</p>
            <p>{`Category: ${transaction.category}`}</p>
            <p>{`Date: ${transaction.date}`}</p>
            <p>{`Description: ${transaction.description}`}</p>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Category</h2>
        {Object.entries(summarizeByCategory()).map(([category, totalAmount]) => (
          <div key={category}>
            <p>{`${category}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Date</h2>
        {Object.entries(summarizeByDate()).map(([date, totalAmount]) => (
          <div key={date}>
            <p>{`${date}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Month</h2>
        {Object.entries(summarizeByMonth()).map(([month, totalAmount]) => (
          <div key={month}>
            <p>{`${month}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Year</h2>
        {Object.entries(summarizeByYear()).map(([year, totalAmount]) => (
          <div key={year}>
            <p>{`${year}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
      <button onClick={handleShowTransactions}>Show Transactions</button>
      <button onClick={handleAddTransaction}>Add Transaction</button>
      <button onClick={handleDeleteTransaction}>Delete Transaction</button>
      <button onClick={handleSummarizeByDate}>Summarize by Date</button>
      <button onClick={handleSummarizeByMonth}>Summarize by Month</button>
      <button onClick={handleSummarizeByYear}>Summarize by Year</button>
      <button onClick={handleSummarizeByCategory}>Summarize by Category</button>
      <button onClick={handleQuit}>Quit</button>
    </div>
  );
        }
