import React, { useState, useEffect } from 'react';
import './App.css';

// import initial transaction list
import initialTransactions from "./InitialTransaction"

function transaction() {
  // initialize state variables
  const [transactions, setTransactions] = useState([]);
  const [amount, setAmount] = useState(0);
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');

  // get initial transaction list from localStorage, or use the default list
  useEffect(() => {
    const storedTransactions = JSON.parse(localStorage.getItem('transactions'));
    if (storedTransactions) {
      setTransactions(storedTransactions);
    } else {
      setTransactions(initialTransactions);
    }
  }, []);

  // update localStorage whenever the transactions state changes
  useEffect(() => {
    localStorage.setItem('transactions', JSON.stringify(transactions));
  }, [transactions]);

  // add new transaction to the list
  const handleAddTransaction = () => {
    const newTransaction = {
      itemId: transactions.length + 1,
      amount: parseFloat(amount),
      category,
      date: new Date().toISOString(),
      description,
    };
    setTransactions([...transactions, newTransaction]);
    setAmount(0);
    setCategory('');
    setDescription('');
  };

  // delete transaction from the list
  const handleDeleteTransaction = (itemId) => {
    const updatedTransactions = transactions.filter((t) => t.itemId !== itemId);
    setTransactions(updatedTransactions);
  };

  // summarize transactions by date
  const summarizeByDate = () => {
    const summarizedData = transactions.reduce((acc, curr) => {
      const date = curr.date.substring(0, 10);
      if (acc[date]) {
        acc[date] += curr.amount;
      } else {
        acc[date] = curr.amount;
      }
      return acc;
    }, {});
    return summarizedData;
  };

  // summarize transactions by month
  const summarizeByMonth = () => {
    const summarizedData = transactions.reduce((acc, curr) => {
      const month = curr.date.substring(0, 7);
      if (acc[month]) {
        acc[month] += curr.amount;
      } else {
        acc[month] = curr.amount;
      }
      return acc;
    }, {});
    return summarizedData;
  };

  // summarize transactions by year
  const summarizeByYear = () => {
    const summarizedData = transactions.reduce((acc, curr) => {
      const year = curr.date.substring(0, 4);
      if (acc[year]) {
        acc[year] += curr.amount;
      } else {
        acc[year] = curr.amount;
      }
      return acc;
    }, {});
    return summarizedData;
  };

  // summarize transactions by category
  const summarizeByCategory = () => {
    const summarizedData = transactions.reduce((acc, curr) => {
      if (acc[curr.category]) {
        acc[curr.category] += curr.amount;
      } else {
        acc[curr.category] = curr.amount;
      }
      return acc;
    }, {});
    return summarizedData;
  };

  return (
    <div className="App">
      <h1>Finance Tracker</h1>
      <div className="form">
        <label>
          Item ID:
          <input type="text" name="itemId" value={itemId} onChange={handleChange} />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={amount} onChange={handleChange} />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={category} onChange={handleChange} />
        </label>
        <label>
          Date:
          <input type="date" name="date" value={date} onChange={handleChange} />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={description} onChange={handleChange} />
        </label>
        <button onClick={handleSubmit}>Add Transaction</button>
      </div>
      <div>
        <h2>Transactions</h2>
        {transactions.map((transaction, index) => (
          <div key={index}>
            <p>{`Item ID: ${transaction.itemId}`}</p>
            <p>{`Amount: ${transaction.amount}`}</p>
            <p>{`Category: ${transaction.category}`}</p>
            <p>{`Date: ${transaction.date}`}</p>
            <p>{`Description: ${transaction.description}`}</p>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
      <div>
        <h2>Summary by Category</h2>
        {Object.entries(summarizeByCategory()).map(([category, totalAmount]) => (
          <div key={category}>
            <p>{`${category}: ${totalAmount}`}</p>
          </div>
        ))}
      </div>
    </div>
  );
        }
